mapper.select:
select * from plan_attraction
AND plan_id = #{planId}
AND attraction_id = #{attractionId}
ORDER BY sequence asc

mapper.update:
update
	plan_attraction

	sequence = #{sequence}


	plan_id = #{planId} AND attraction_id = #{attractionId}

mapper.delete:
delete
from
	plan_attraction



			AND attraction_id = #{attractionId}


			AND plan_id = #{planId}
