mapper.select:
select plan.plan_id, plan.plan_name, plan.created_at, plan.visibility, plan.user_id, user.id as author, COALESCE(bookmark_count, 0) AS bookmark, COALESCE(favor_count, 0) AS favor, COALESCE(comment_count, 0) AS comment from plan left join user on plan.user_id = user.user_id left join ( select plan_user_bookmark_favor.plan_id, SUM(case when bookmark = true then 1 else 0 end) as bookmark_count, SUM(case when favor = true then 1 else 0 end) as favor_count from plan_user_bookmark_favor group by plan_user_bookmark_favor.plan_id )as plan_user_bookmark_favor on plan.plan_id = plan_user_bookmark_favor.plan_id left join ( select plan_comment.plan_id, count(plan_comment.plan_id) as comment_count from plan_comment group by plan_comment.plan_id )as plan_comment on plan.plan_id = plan_comment.plan_id
AND (select count(*) from plan_attraction where plan_attraction.plan_id = plan.plan_id and plan_attraction.attraction_id in
#{attractionId}
) = #{attractionIdList.size()}
AND plan.visibility=#{visibility}
AND plan.plan_name like CONCAT('%',#{planName},'%')
AND EXISTS( select 1 from plan_member where user_Id=#{memberUserId} and plan.plan_id = plan_member.plan_id)
AND plan.user_id = #{authorUserId}
AND plan.plan_id = #{planId}
order by
plan.plan_name ASC
plan.plan_name DESC
read_count ASC
read_count DESC
bookmark ASC
bookmark DESC
favor ASC
favor DESC
comment asc
comment desc
plan.plan_name ASC
limit #{index},#{pageSize}
